<template>
    <div>
        <header>
            <h2>
                <router-link
                        :to="{ name: 'dashboard' }"
                >
                    Dashboard
                </router-link>
                /
                <router-link
                        :to="{ name: 'actionTrip' }"
                >
                    Voyage
                </router-link>
                /
                <span class="current__breadcrump">Proposer un voyage</span>
            </h2>
        </header>
        <div class="separation"></div>
        <div class="content_main">
            <form-wizard
                    color="rgba(var(--vs-primary), 1)"
                    :title="null"
                    :subtitle="null"
                    finishButtonText="Envoyer"
                    @on-complete="addTrip"
                    back-button-text="Précédent"
                    next-button-text="Suivant"
            >
                    <tab-content title="Informations" class="mb-5 profile-card" icon="fas fa-info" :before-change="validateStep1">
                        <!-- tab 1 content -->
                        <vs-row>
                            <vs-col vs-type="flex" vs-justify="center" vs-align="center" w="7">
                                <vs-row>
                                    <vs-col vs-type="flex" vs-justify="center" vs-align="center" w="6">
                                        <vs-input
                                                label-placeholder="Petit nom pour votre voyage"
                                                v-model="trip.title"
                                                class="input__add__trip"
                                        >
                                            <template v-if="errors.includes('title')" #message-danger>
                                                Un titre pour votre voyage est requis
                                            </template>
                                        </vs-input>
                                    </vs-col>
                                    <vs-col vs-type="flex" vs-justify="center" vs-align="center" w="6">
                                        <vs-input
                                                type="number"
                                                v-model="trip.nbTravelers"
                                                label="Nombre de voyageurs recherchés"
                                                min="1"
                                                max="11"
                                                class="input__add__trip"
                                        />
                                    </vs-col>
                                    <vs-col vs-type="flex" vs-justify="center" vs-align="center" w="12">
                                        <label for="description__trip" class="label__textaera">Courte présentation de votre voyage</label>
                                        <textarea
                                            id="description__trip"
                                            v-model="trip.description"
                                            class="textaera__add__trip"
                                            rows="16"
                                        >
                                        </textarea>
                                        <div v-if="errors.includes('description')" class="msg__error">
                                            Une courte description est requise
                                        </div>
                                    </vs-col>
                                </vs-row>
                            </vs-col>
                            <vs-col vs-type="flex" vs-justify="center" vs-align="center" w="5">
