                                                placeholder="Pays"
                                                v-model="trip.country"
                                                :state="primary"
                                        >
                                            <vs-option :key="index" :label="item.name" :value="item.name" v-for="(item,index) in this.countries">
                                                {{ item.name }}
                                            </vs-option>
                                        </vs-select>
                                    </vs-col>
                                    <vs-col vs-justify="center" vs-align="center" w="6" v-if="trip.country">
                                        <vs-input label="Ville" v-model="trip.city"/>
                                    </vs-col>
                                </vs-row>
                                <h3 style="margin-top: 20px">Récapitulatif</h3>
                                <p style="margin-top: 10px">
                                    Notre destination sera : <b>{{ trip.country ? trip.country : "..." }}</b>
                                    <span v-if="trip.country">
                                        <span v-if="trip.city">
                                            dans la ville de <b>{{ trip.city }}</b>
                                        </span>
                                        <span v-else>
                                             dans une ville que nous ne connaissons pas encore
                                        </span>
