                    <!-- tab 5 content -->
                    <tab-content title="Sur place" class="mb-5 profile-card" icon="fas fa-sun">
                        <vs-row>
                            <vs-col vs-justify="center" vs-align="center" w="7">
                                <vs-row vs-type="flex" style="justify-content: space-between; align-items: center;">
                                    <vs-col w="6">
                                        <h3>Activités</h3>
                                    </vs-col>
                                    <vs-col vs-type="flex" vs-justify="flex-end" w="6">
                                        <vs-button @click="activePrompt = true" type="relief" class="btn-perso" style="margin-left: auto;">Ajouter une activité</vs-button>
                                    </vs-col>
                                </vs-row>

                                <vs-table
                                        :data="this.trip.activities"
                                        style="margin-top: 10px"
                                        noDataText="Pas d'activités prévues"
                                >
                                    <template slot="thead">
                                        <vs-th>
                                            Nom de l'activité
                                        </vs-th>
                                        <vs-th>
                                            Type de l'activité
                                        </vs-th>
                                        <vs-th>
                                            Volonté
                                        </vs-th>
                                        <vs-th>
                                            Action
                                        </vs-th>
                                    </template>

                                    <template slot-scope="{data}">
                                        <vs-tr :key="indextr" v-for="(tr, indextr) in data" >
                                            <vs-td :data="tr.name">
                                                {{ tr.name }}
                                            </vs-td>

                                            <vs-td :data="tr.type">
                                                {{ tr.type }}
                                            </vs-td>

                                            <vs-td :data="tr.wish">
                                                <vs-tooltip text="tr.wish %">
                                                    <vs-progress v-if="tr.wish > 80" :percent="tr.wish" color="success">{{tr.wish}} %</vs-progress>
                                                    <vs-progress v-else-if="tr.wish > 60" :percent="tr.wish" color="primary">{{tr.wish}} %</vs-progress>
                                                    <vs-progress v-else-if="tr.wish > 30" :percent="tr.wish" color="warning">{{tr.wish}} %</vs-progress>
                                                    <vs-progress v-else :percent="tr.wish" color="danger">{{tr.wish}} %</vs-progress>
                                                </vs-tooltip>
                                            </vs-td>

                                            <vs-td :data="tr.name">
                                                <vs-button @click="deleteActivity(indextr)" radius type="relief" color="primary" icon="delete"></vs-button>
                                            </vs-td>
                                        </vs-tr>
                                    </template>
                                </vs-table>
                                <vs-dialog
                                        :is-valid="validatedActivity"
                                        title="Ajouter une activité"
                                        class="modal-perso"
                                        accept-text="Ajouter"
                                        v-model="activePrompt"
                                >
                                    <template #header>
                                        <h4 class="not-margin">
                                            Ajout d'une <b>activité</b>
                                        </h4>
                                    </template>
                                    <div class="con-content">
                                        <vs-input label="Nom de l'activité" v-model="activity.name" style="margin-bottom: 5px;"/>
                                        <vs-row vs-type="flex" style="margin-top: 30px;">
                                            <vs-col w="7">
                                                <vs-input label="Type de l'activité" v-model="activity.type" style="margin-bottom: 5px;"/>
                                            </vs-col>
                                            <vs-col w="5">
                                                <vs-input
                                                        label="Volonté"
                                                        type="number"
                                                        v-model="activity.wish"
                                                        min="0"
                                                        max="100"
                                                        class="input__in__text"
                                                />
                                            </vs-col>
                                        </vs-row>
                                    </div>
                                    <template #footer>
                                        <div class="con-footer">
                                            <vs-button @click="activePrompt=false" dark transparent>
                                                Fermer
                                            </vs-button>
                                            <vs-button @click="validActivity" transparent>
                                                Ajouter
                                            </vs-button>
                                        </div>
                                    </template>
                                </vs-dialog>
                            </vs-col>
                            <vs-col vs-justify="center" vs-align="center" w="5">
                                <div class="step-form-wizard">
