<script>
    import {FormWizard, TabContent} from 'vue-form-wizard';
    import Cleave from 'cleave.js';
    import axios from 'axios';
    import moment from "moment";

    const feather = require('feather-icons');

    export default {
        name: 'AddTrip',
        props: {
            user: Object
        },
        data: () => ({
            errors: [],
            activePrompt:false,
            trip: {
                title: '',
                nbTravelers: 1,
                budget: {
                    min: 0,
                    max: 5000
                },
                date: '',
                days: 1,
                flexibilities: [],
                country: '',
                city: '',
                activities: [],
                description: ''
            },
            activity: {
                name: '',
                wish: 50,
                type: '',
            },
            countries: JSON
        }),
        mounted() {
            feather.replace();
            this.cleave = new Cleave('.input-date-naiss input', {
                date: true,
                delimiter: ' / ',
                datePattern: ['d', 'm', 'Y']
            });
            axios
                .get('https://restcountries.eu/rest/v2/all')
                .then(response => (this.countries = response.data))
            ;
        },
        components: {
            FormWizard,
            TabContent
        },
        computed: {
            validatedActivity(){
                return (this.activity.name.length > 0 && this.activity.type.length > 0);
            }
        },
        methods: {
            cancelActivity() {
                this.resetActivity();
            },
            validActivity() {
                this.trip.activities.push({
                    name: this.activity.name,
                    type: this.activity.type,
                    wish: this.activity.wish
                });
                this.resetActivity();
                this.activePrompt = false;
            },
            resetActivity() {
                this.activity.name='';
                this.activity.type='';
                this.activity.wish=50;
            },
            deleteActivity(activity) {
                this.trip.activities.splice(activity, 1);
            },
            validateStep1() {
                this.errors = [];

                if(this.trip.title === "") {
                    this.errors.push('title');
                }
                if (this.trip.nbTravelers === "") {
                    this.trip.nbTravelers = 1
                }
                if(this.trip.description === "") {
                    this.errors.push('description');
                }

                return this.errors.length <= 0;
            },
            validateStep3() {
                this.errors = [];

                if(!(moment(this.trip.date, "DD/MM/YYYY").isValid()) || (moment(this.trip.date, "DD/MM/YYYY").diff(moment())) <= 0) {
                    this.errors.push('date');
                }

                return this.errors.length <= 0;
            },
            validateStep4() {
                this.errors = [];

                if(this.trip.country === ""){
                    this.errors.push('country');
                }

                return this.errors.length <= 0;
            },
            addTrip() {
                this.$vs.loading();
                axios({
                    method: 'post',
                    url: '/api/add-trip',
                    data: this.trip
                })
                .then(response => {
                    this.$vs.loading.close();
                    this.$vs.notify({title:'Ajout réussi',text:'La proposition de votre voyage a bien été ajoutée',color:'success',icon:'check_box'})
                })
                .catch(response => {
                    this.$vs.loading.close();
                    this.$vs.notify({title:'Erreur',text:'Une erreur interne est survenue, veuillez réessayer utltérieurement',color:'danger',icon:'error'})
                })
            }
        }
    }
</script>
